"use strict";
// Type inference means
// typescript infers the type when we don't explicitly annotate them.
//string
let employeeName = "preethi"; //this is type inference
console.log(`(string) Employee Name: ${employeeName}`);
//number
let empAge = 45;
console.log(`(number) Employee Age: ${empAge}`);
//boolean
let isFemale = "true";
console.log(`(boolean) is Female: ${isFemale}`);
